import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import { FaEdit, FaTrash } from 'react-icons/fa';
import DeleteModal from '../components/DeleteModal';

const url = 'http://localhost:3001/api/book';

function Home() {
  const [users, setUsers] = useState([]);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState({});
  const fetchUsers = () => {
    fetch(url)
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setUsers(data.payload);
      })
      .catch(() => {
        setUsers([]);
      });
  };
  const deleteBook = (id) => {
    fetch(`${url}/${id}`, { method: 'DELETE' })
      .then((response) => {
        return response.json();
      })
      .then(() => {
        fetchUsers();
        setShowDeleteModal(false);
      })
      .catch(() => {});
  };
  const handleDeleteBookClick = (book) => {
    setShowDeleteModal(true);
    setSelectedUser({ ...book });
  };
  useEffect(() => {
    fetchUsers();
  }, []);
  return (
    <div className='container'>
      <div className='header-container'>
        <h1 className='mb-3 mt-5'>Book List</h1>
        <Link to='save'>
          <Button variant='primary'>Create Book</Button>
        </Link>
      </div>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>User Name</th>
            <th>RegNo</th>
            <th>Address</th>
            <th>URL</th>
            <th>Pickup Place</th>
            <th>BusNo</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {users?.length
            ? users.map((book) => {
                return (
                  <tr key={book._id}>
                    <td>{book.bookName}</td>
                    <td>{book.author}</td>
                    <td>{book.noOfBook}</td>
                    <td>{book.publisher}</td>
                    <td>{book.url}</td>
                    <td>{book.price}</td>
                    <td>
                      <Link to={`/save/${book._id}`}>
                        <FaEdit />
                      </Link>
                    </td>
                    <td>
                      <FaTrash onClick={() => handleDeleteBookClick(book)} />
                    </td>
                  </tr>
                );
              })
            : null}
        </tbody>
      </Table>
      <DeleteModal
        showDeleteModal={showDeleteModal}
        setShowDeleteModal={setShowDeleteModal}
        {...selectedUser}
        deleteBook={deleteBook}
      />
    </div>
  );
}

export default Home;
